func PartOne(directions string) int64 {
	currentFloor := int64(0)

	for i := range directions {
		if string(directions[i]) == "(" {
			currentFloor += 1
		} else {
			currentFloor -= 1
		}
	}
	return currentFloor
}

func PartTwo(directions string) int64 {
	currentFloor := int64(0)
	moves := int64(0)

	for i := range directions {
		moves++

		if string(directions[i]) == "(" {
			currentFloor += 1
		} else {
			currentFloor -= 1
		}

		if currentFloor == -1 {
			return moves
		}
	}

	return 0
}

