func partOne(directions string) int {
	currentFloor := 0
	for i := range directions {
		if string(directions[i]) == "(" {
			currentFloor += 1
		} else {
			currentFloor -= 1
		}
	}
	return currentFloor
}

func partTwo(directions string) int {
	currentFloor := 0
	moves := 0

	for i := range directions {
		moves++

		if string(directions[i]) == "(" {
			currentFloor += 1
		} else {
			currentFloor -= 1
		}

		if currentFloor == -1 {
			return moves
		}
	}

	return 0
}

