package main

import (
	"fmt"
	"strings"
)

func findMarker(a, b, c, d string) bool {
	if a != b && a != c && a != d {
		if b != c && b != d {
			if c != d {
				fmt.Println(a, b, c, d)
				return true
			}
		}
	}
	return false
}

func main() {
	input := ""

	s := strings.Split(input, "")

	p1 := 0

	for i := range s {
		isMarker := findMarker(s[i], s[i+1], s[i+2], s[i+3])
		if isMarker {
			p1 = i + 4
			break
		}
	}

	fmt.Println(p1)
}
